import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [api, setApi] = useState('')

  useEffect(() => {
    // Function to fetch all users using Axios
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        console.log(response.data)
        setUsers(response.data); // Assuming the response contains user data
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    // Call the fetchUsers function
    fetchUsers();
  }, []);

  const blockUser = (userId) => {
    console.log('Block user:', userId);
    axios.put(`http://localhost:5000/api/users/block/${userId}`).then((response) => {
      console.log(response);
      // Update the user list after deletion
    }).catch((err) => {
      console.log(err);
    });
  };

  const deleteUser = (userId) => {
    console.log('Delete user:', userId);
    axios.delete(`http://localhost:5000/api/users/${userId}`).then((response) => {
      console.log(response);
      // Update the user list after deletion
      const updatedUsers = users.filter((user) => user.tele_id !== userId);
      setUsers(updatedUsers);
    }).catch((err) => {
      console.log(err);
    });
  };
  




const handleSubmit = (e) => {
    e.preventDefault();
    const newChat ={
        api: api,
    };
    const objectId= "658c1ac43b9561d7d764dddb";
    axios.post('http://localhost:5000/api/users/',newChat)
    .then((response) => {
        console.log('User registered',response.data)
    })
    .catch((error)=>{
        console.error('Error registered user:', error)
    })
}

  return (
    <div>
      <h1>Welcome to the Admin Panel</h1>
      <h2>User List</h2>
      <ul>
        {users.map((user, userIndex) => (
            <li key={userIndex}> {user.tele_id} -- {user.location} {user.username}  
              <button onClick={() => deleteUser(user.tele_id)}> Remove User </button>
              {user.isBlocked == true ? (
            <button onClick={() => blockUser(user.tele_id)}> Unblock User </button>
          ) : (
            <button onClick={() => blockUser(user.tele_id)}> Block User </button>
          )}

</li>
        ))} 
      </ul>
      <form onSubmit={handleSubmit}>
             <label htmlFor="fname">Full Name</label>
             <input type="text" className="input_form" id="fname" name="fname" 
             value={api}
             onChange={(e)=> setApi(e.target.value)}/>
             <input type="submit" className="btn_form" value="Send"/>
        </form>
    </div>
  );
}
