import { Link } from 'react-router-dom';
import Cookies from "js-cookie";
import Home from '../Home';
import React, { useState } from 'react';


function Dashboard() {
  const [isTokenExists, setIsTokenExists] = useState(false);

  const handleGoogleLogin = () => {
    try {
      // Open a popup window for Google authentication
      const popup = window.open('http://localhost:5000/google', 'Google Auth', 'width=600,height=600');

      retrieveToken();
      // const checkPopupClosed = setInterval(() => {
      //   if (!popup || popup.closed || popup.closed === undefined) {
      //     clearInterval(checkPopupClosed);
      //     // The popup is closed, do something after authentication completes
      //     // For instance, you can check if the user is authenticated, then close the popup
      //     // After the authentication is done, close the popup
      //     // For demonstration purposes, assuming the authentication process sets a flag in localStorage
      //     if (document.getItem('isAuthenticated')) {
      //       popup.close(); // Close the popup window
      //     }
      //   }
      // }, 1000); // Check every second if the popup is closed
    } catch (error) {
      setIsTokenExists(false);

      console.error('Error logging in with Google:', error);
    }
  };


  const retrieveToken = () => {
    const token = Cookies.get('token');
    if (token) {
      // Store the token in localStorage for future use
      setIsTokenExists(true);
      localStorage.setItem('token', token);
      // Do further actions like navigating to a protected route or making authorized API calls
    } else {
      setIsTokenExists(false);
      console.log('Token cookie does not exist or is not accessible.');
    }
  };

  return (
    <div>
       {isTokenExists ? (
        <Home />
      ) : (
        // Display this content when the token does not exist
        <div>
          <h1>Welcome</h1>
          <button onClick={handleGoogleLogin}>Login with Google</button>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
