import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AdminPanel() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Function to fetch all users using Axios
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        console.log(response.data)
        setUsers(response.data); // Assuming the response contains user data
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    // Call the fetchUsers function
    fetchUsers();
  }, []);

  const blockUser = (userId) => {
    console.log('Block user:', userId);
    // Add logic to block user by sending a request to your backend
  };

  const deleteUser = (userId) => {
    console.log('Delete user:', userId);
    axios.delete(`http://localhost:5000/api/users/${userId}`).then((response) => {
      console.log(response);
      // Update the user list after deletion
      const updatedUsers = users.filter((user) => user.tele_id !== userId);
      setUsers(updatedUsers);
    }).catch((err) => {
      console.log(err);
    });
  };
  
  return (
    <div>
      <h1>Welcome to the Admin Panel</h1>
      <h2>User List</h2>
      <ul>
        {users.map((user, userIndex) => (
            <li key={userIndex}> {user.tele_id} -- {user.location} {user.username}  
            <button onClick={() => deleteUser(user.tele_id)}> Remove User </button>
            <button onClick={() => blockUser(user.tele_id)}> Block User </button>            
</li>
        ))}
      </ul>
    </div>
  );
}
