import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import '../Customers/signup.css'


function Customers() {

  useEffect(() => {
    // Retrieve the 'token' cookie value
    const token = Cookies.get('token');

    if (token) {
      console.log('Token:', token);
      console.log(document.cookie)
      // Perform actions with the token here
    } else {
      console.log('Token cookie does not exist or is not accessible.');
      console.log(document.cooke)
    }
  }, []);


  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [username, setUsername] = useState('');


  const url = process.env.BACK_END_URL;
  const handleLogin = (e) => {
    console.log("hi")
    console.log(fullname,email,pass,username)
    e.preventDefault();
    console.log("clicked here")
     const SignUpData = {
      fullname : fullname,
      email : email,
      pass : pass,
      username : username
    };
      axios.post(`http://localhost:5000/api/signupdata`, SignUpData)
      .then((response)=> {
        console.log(process.env.BACK_END_URL);
          console.log('Success', response)
          if(response.status === 200){
                console.log("Success");
          }
        })
      .catch((err) => {
          console.log('There is an error',err)

          if(err.response.status === 409){
              console.log("not found");
          }
          else if(err.response.status === 400){
            console.log("fail")
          }
      })
    }

  return (
    <>
     <div className='signup'>
    <div className="container-signup">
      <div className="brand-container-signup">
        <h1 className="header-signup header-left-signup">
            Get Daily weather updates 
            {/* Sign Up Through */}
            </h1>
        <div className="icon-media-signup">
          <i className="fa-brands fa-facebook fa-xl i"></i>
          <i className="fa-brands fa-instagram fa-xl i"></i>
          <i className="fa-brands fa-twitter fa-xl i"></i>
        </div>
      </div>
      <div className="form-container-signup">
        <h1 className="header-signup header-right-signup">Please Subscribe......</h1>

 <form action="#" onSubmit={handleLogin}>
        <input type="text" name="fullname" placeholder="Location" 
          value={fullname}
          onChange={(e)=>setFullname(e.target.value)}
          // required
          />
          <input type="text" name="email" placeholder="Telegram UserId" 
          value={email}
          onChange={(e)=>setEmail(e.target.value)}
          // required
          />
          <input type="text" name="pass" placeholder="Notification Preferences" 
          value={pass}
          onChange={(e)=>setPass(e.target.value)}
          // required
          />
          <input type="text" name="username" id="username" placeholder="Username: How We should call you" 
          value={username}
          onChange={(e)=>setUsername(e.target.value)}
          // required
          />
          <button type="submit" className="btn btn-danger">Subscribe</button>
        </form>
        </div>
    </div>

    </div>
    </>
  );
}
export default Customers;
