      const handleGoogleLogin = async () => {
        try {
          // Redirect to Google authentication endpoint on your backend
          window.location.href = 'http://localhost:5000/google'; // Replace with your actual endpoint
        } catch (error) {
          console.error('Error logging in with Google:', error);
        }
      };


        return (
          <div className='App'>
            <GoogleOAuthProvider clientId='1028953907365-4bh5l4c74dinjmur0klu663p1kbdfhdg.apps.googleusercontent.com'>
              <div>
                  <GoogleLogin
                    useOneTap
                    onSuccess={async (credentialResponse) => {
                      const response = await axios.post(
                        'http://localhost:3001/login',
                        {
                          token: credentialResponse.credential,
                        }
                      );
                      const data = response.data;

                      localStorage.setItem('authData', JSON.stringify(data));
                      setAuthData(data);
                    }}
                    onError={() => {
                      console.log('Login Failed');
                    }}
                  />
      l        </div>

              <Profile />
            </GoogleOAuthProvider>
          </div>
        );
      }



  export default Dashboard;
