import { Avatar, Rate, Space, Table, Typography } from "antd";
import { useEffect, useState } from "react";
import { getCustomers, getInventory } from "../../API";

function Customers() {
  const [loading, setLoading] = useState(false);
  const [dataSource, setDataSource] = useState([]);

  useEffect(() => {
    setLoading(true);
    getCustomers().then((res) => {
      setDataSource(res.users);
      setLoading(false);
    });
  }, []);

  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [username, setUsername] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
     const SignUpData = {
      fullname : fullname,
      email : email,
      pass : pass,
      username : username
    };
      axios.post(`http://${url}:5000/api/signupdata`, SignUpData)
      .then((response)=> {

          console.log('Success', response)
          if(response.status === 200){
              setpopUpSuccess('block');
              setTimeout(() => {
                onSignUpFormSubmit()
                setpopUpSuccess('none'); // Hide popup after 5 seconds
              }, 2000);

          }
        })
      .catch((err) => {
          console.log('There is an error',err)

          if(err.response.status === 409){
              console("Success");
          }
          else if(err.response.status === 400){
            console.log("fail")
          }
      })
    }



  return (
    <>
 <form action="#" onSubmit={handleLogin}>
        <input type="fullname" name="fullname" id="fullname" placeholder="Full Name" 
          value={fullname}
          onChange={(e)=>setFullname(e.target.value)}
          required
          />
          <input type="email" name="email" id="email1" placeholder="Email ID" 
          value={email}
          onChange={(e)=>setEmail(e.target.value)}
          required
          />
          <input type="password" name="pass" id="pass1" placeholder="Password" 
          value={pass}
          onChange={(e)=>setPass(e.target.value)}
          required
          />
          <input type="username" name="username" id="username" placeholder="Username: How We should call you" 
          value={username}
          onChange={(e)=>setUsername(e.target.value)}
          required
          />
          <button type="submit">Sign-Up</button>
        </form>
    <Space size={20} direction="vertical">
      <Typography.Title level={4}>Customers</Typography.Title>
      <Table
        loading={loading}
        columns={[
          {
            title: "Photo",
            dataIndex: "image",
            render: (link) => {
              return <Avatar src={link} />;
            },
          },
          {
            title: "First Name",
            dataIndex: "firstName",
          },
          {
            title: "LastName",
            dataIndex: "lastName",
          },
          {
            title: "Email",
            dataIndex: "email",
          },
          {
            title: "Phone",
            dataIndex: "phone",
          },

          {
            title: "address",
            dataIndex: "address",
            render: (address) => {
              return (
                <span>
                  {address.address}, {address.city}
                </span>
              );
            },
          },
        ]}
        dataSource={dataSource}
        pagination={{
          pageSize: 5,
        }}
      ></Table>

    </Space>
    </>
  );
}
export default Customers;
