import React, { useState, useEffect } from 'react';
import './app.css';

const Weathercard = ({ tempinfo }) => {
  const [weatherState, setWeatherState] = useState('');
  const {
    temp,
    humidity,
    pressure,
    weathermood,
    name,
    speed,
    country,
    sunset,
  } = tempinfo;

  useEffect(() => {
    // Function to update weather state based on weathermood
    const updateWeatherState = () => {
      if (weathermood) {
        switch (weathermood) {
          case 'Clouds':
            setWeatherState('wi-day-cloudy');
            break;
          case 'Haze':
            setWeatherState('wi-fog');
            break;
          case 'Clear':
            setWeatherState('wi-day-sunny');
            break;
          case 'Mist':
            setWeatherState('wi-dust');
            break;
          default:
            setWeatherState('wi-day-sunny');
            break;
        }
      }
    };

    updateWeatherState();
  }, [weathermood]);

  // Function to fetch weather data
  const getWeatherInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${name}&units=metric&appid=75b31160e847eff782e127fda7c5665e`;
      const res = await fetch(url);
      const data = await res.json();

      const { temp, humidity, pressure } = data.main;
      const { main: weathermood } = data.weather[0];
      const { speed } = data.wind;
      const { country, sunset } = data.sys;

      const updatedWeatherInfo = {
        temp,
        humidity,
        pressure,
        weathermood,
        name,
        speed,
        country,
        sunset,
      };

      setWeatherState('');
      setTimeout(() => {
        setWeatherState(tempinfo.weathermood);
      }, 10); // Delay setting the state to ensure the state changes trigger the useEffect

      setTempInfo(updatedWeatherInfo);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
       <article className='widget'>
            <div className='weatherIcon'>
                <i className={`wi ${weatherState}`}></i>
            </div>
            <div className='weatherInfo'>
                <div className='temperature'>
                    <span>{temp}&deg;</span>
                </div>               
            
            <div className='description'>
            <div className='weatherCondition'>{weathermood}</div>
                <div className='place'>
                    {name}, {country}
                </div>
            </div>
            </div>
            <div className='date'>
                    {new Date().toLocaleString()}
            </div>
            {/* 4 column section */}
            <div className='extra-temp'>
                <div className='temp-info-minmax'>
                    <div className='two-sided-section'>
                        <p>
                            <i className={'wi wi-sunset'}></i>
                            </p>
                        <p className='extra-info-leftside'> {timeStr} <br/>
                        Sunset
                        </p>

                    </div>
                    <div className='two-sided-section'>
                        <p>
                            <i className={'wi wi-humidity'}></i>
                            </p>
                        <p className='extra-info-leftside'>{humidity}<br/>
                        Humidity
                        </p>

                    </div>
                </div>
                <div className='weather-extra-info'>
                <div className='two-sided-section'>
                        <p>
                            <i className={'wi wi-rain'}></i>
                            </p>
                        <p className='extra-info-leftside'> {pressure} <br/>
                        Pressure
                        </p>

                    </div>
                    <div className='two-sided-section'>
                        <p>
                            <i className={'wi wi-strong-wind'}></i>
                            </p>
                        <p className='extra-info-leftside'> {speed}<br/>
                        Speed
                        </p>

                    </div>
                </div>
            </div>
        </article>
    </>
  )
}

export default Weathercard

