import { Card, Space, Statistic, Table, Typography } from "antd";
import { useEffect, useState } from "react";
import { getCustomers, getInventory, getOrders, getRevenue } from "../../API";
import Weathercard from "./Weathercard";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function Dashboard() {
  const [orders, setOrders] = useState(0);
  const [inventory, setInventory] = useState(0);
  const [customers, setCustomers] = useState(0);
  const [revenue, setRevenue] = useState(0);

  useEffect(() => {
    getOrders().then((res) => {
      setOrders(res.total);
      setRevenue(res.discountedTotal);
    });
    getInventory().then((res) => {
      setInventory(res.total);
    });
    getCustomers().then((res) => {
      setCustomers(res.total);
    });
  }, []);

  const [searchValue,setSearchValue]= useState("sibsagar");
  const [tempInfo, setTempInfo] = useState({}); //empty array passsing initially
  //for the first time by default it should get something
  const getWeatherInfo = async()=> {
      try{
          let url=`https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=75b31160e847eff782e127fda7c5665e`;
          const res= await fetch(url);
          const data= await res.json();

          const {temp,humidity,pressure} =data.main;
          console.log(temp);
          console.log(humidity);
          console.log(pressure);
          const {main:weathermood}=data.weather[0];
          const {name} =data;
          const {speed} =data.wind;
          const {country,sunset} =data.sys; 
          //object creating out of weather properties
          const myNewWeatherInfo ={
              temp, humidity, pressure, weathermood, name, speed, country, sunset
          };
          setTempInfo(myNewWeatherInfo);
      }catch(error){
          console.log(error);
      }
  };
  //automatic function call just after refreshing the page is can be done by useEffect hook
  useEffect(() => {getWeatherInfo()}, []);//passing an empty array that means value reflects on the only very first time the page is refreshed




  return (
    <Space size={20} direction="vertical">
      <Typography.Title level={4}> <button>Subscribe </button> for getting Daily Updates on Telegram </Typography.Title>
      <Space direction="horizontal">
      <div className="wrap">
      <div className="search">
        <input
          type="search"
          placeholder="search..."
          autoFocus
          id="search"
          className="searchTerm"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />

        <button
          className="searchButton"
          type="button"
          onClick={getWeatherInfo}
          >
          Search
        </button>
      </div>
      </div>

     <Weathercard tempinfo={tempInfo}/>

      </Space>
    </Space>
  );
}

function DashboardCard({ title, value, icon }) {
  return (
    <Card>
      <Space direction="horizontal">
        {icon}
        <Statistic title={title} value={value} />
      </Space>
    </Card>
  );
}
function RecentOrders() {
  const [dataSource, setDataSource] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    getOrders().then((res) => {
      setDataSource(res.products.splice(0, 3));
      setLoading(false);
    });
  }, []);

  return (
    <>
      <Typography.Text>Recent Orders</Typography.Text>
      <Table
        columns={[
          {
            title: "Title",
            dataIndex: "title",
          },
          {
            title: "Quantity",
            dataIndex: "quantity",
          },
          {
            title: "Price",
            dataIndex: "discountedPrice",
          },
        ]}
        loading={loading}
        dataSource={dataSource}
        pagination={false}
      ></Table>
    </>
  );
}

function DashboardChart() {
  const [reveneuData, setReveneuData] = useState({
    labels: [],
    datasets: [],
  });

  useEffect(() => {
    getRevenue().then((res) => {
      const labels = res.carts.map((cart) => {
        return `User-${cart.userId}`;
      });
      const data = res.carts.map((cart) => {
        return cart.discountedTotal;
      });

      const dataSource = {
        labels,
        datasets: [
          {
            label: "Revenue",
            data: data,
            backgroundColor: "rgba(255, 0, 0, 1)",
          },
        ],
      };

      setReveneuData(dataSource);
    });
  }, []);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "bottom",
      },
      title: {
        display: true,
        text: "Order Revenue",
      },
    },
  };

  return (
    <Card style={{ width: 500, height: 250 }}>
      <Bar options={options} data={reveneuData} />
    </Card>
  );
}
export default Dashboard;
