import { Link } from 'react-router-dom';
import Cookies from "js-cookie";
import AdminPanel from '../AdminPanel/adminPanel';
import React, { useState, useRef } from 'react';


function AdminLogin() {
  const [isTokenExists, setIsTokenExists] = useState(false);
  const popupRef = useRef(null);

  const handleGoogleLogin = () => {
    try {
      // Open a popup window for Google authentication
      const popup = window.open('http://localhost:5000/google', 'Google Auth', 'width=600,height=600');
      popupRef.current = popup;
      retrieveToken();  
    } catch (error) {
      setIsTokenExists(false);

      console.error('Error logging in with Google:', error);
    }
  };


  const retrieveToken = () => {
    const token = Cookies.get('token');
    if (token) {
      // Store the token in localStorage for future use
      setIsTokenExists(true);
      localStorage.setItem('token', token);
      if (popupRef.current) {
        popupRef.current.close();
      }

    } else {
      setIsTokenExists(false);
      console.log('Token cookie does not exist or is not accessible.');
    }
  };

  return (
    <div>
       {isTokenExists ? (
        <AdminPanel />
      ) : (
        // Display this content when the token does not exist
        <div>
          <h1>Welcome Sign In | Admin Panel |</h1>
              <img src="google-signin-button.png" className="google-sign-in" onClick={handleGoogleLogin}/>
        </div>
      )}
    </div>
  );
}

export default AdminLogin;
