import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import Cookies from 'js-cookie';

function Dashboard() {
  const history = useHistory();

  const handleGoogleLogin = async () => {
    try {
      // Redirect to Google authentication endpoint on your backend
      window.location.href = 'http://localhost:5000/google'; // Replace with your actual endpoint
    } catch (error) {
      console.error('Error logging in with Google:', error);
    }
  };

  useEffect(() => {
    // Retrieve the 'token' cookie value
    const token = Cookies.get('token');

    // Use the token as needed (e.g., send it with API requests, store in state, etc.)
    console.log('Token:', token);
    // Perform actions with the token here
  }, []);

  useEffect(() => {
    // Check if there is a query parameter in the URL (callback from Google)
    const urlParams = new URLSearchParams(window.location.search);
    const googleCallback = urlParams.get('googleCallback');

    if (googleCallback === 'success') {
      // If the callback is successful, refresh the route to trigger a re-render
      history.go(0); // Refresh the current route
    }
  }, [history]);

  return (
    <div>
      <h1>Welcome</h1>
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Dashboard;
